import React from "react";
import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import "./Styles/ProductCarousel.css";

const ProductCarousel = ({ title, images }) => {
    const settings = {
        dots: false,
        infinite: true,
        speed: 500,
        slidesToShow: 3,
        slidesToScroll: 1,
        arrows: true,
    };

    return (
        <div className="product-carousel">
            <h2>{title}</h2>
            <Slider {...settings}>
                {images.map((image, index) => (
                    <div className="product-item" key={index}>
                        <img src={image} alt={`Product ${index + 1}`} />
                    </div>
                ))}
            </Slider>
        </div>
    );
};

const ProductCarouselImages = () => {
    // Use relative paths to images in the public/images folder
    const imagesSet1 = [
        "../Assets/C1.jpg",
        "../Assets/C2.jpg",
        "/images/product3.jpg",
        "/images/product4.jpg",
    ];

    const imagesSet2 = [
        "/images/productA.jpg",
        "/images/productB.jpg",
        "/images/productC.jpg",
        "/images/productD.jpg",
    ];

    const imagesSet3 = [
        "/images/productX.jpg",
        "/images/productY.jpg",
        "/images/productZ.jpg",
        "/images/productW.jpg",
    ];

    return (
        <div>
            <ProductCarousel title="Carousel 1" images={imagesSet1} />
            <ProductCarousel title="Carousel 2" images={imagesSet2} />
            <ProductCarousel title="Carousel 3" images={imagesSet3} />
        </div>
    );
};

export default ProductCarouselImages;
