import React from "react";
import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import "./NewsCarousel.css";

const NewsCarousel = () => {
    const settings = {
        dots: true,
        infinite: true,
        speed: 500,
        slidesToShow: 1,
        slidesToScroll: 1,
        arrows: false,
        autoplay: true,
        autoplaySpeed: 3000,
    };

    const newsItems = [
        "Discover the latest local fashion trends!",
        "Black Friday Sales Coming Soon – Stay Tuned!",
        "Free Shipping on Orders Above R500.",
        "Connect with local designers and brands on Modaplug ZA.",
    ];

    return (
        <div className="news-carousel">
            <Slider {...settings}>
                {newsItems.map((item, index) => (
                    <div key={index} className="news-item">
                        <h3>{item}</h3>
                    </div>
                ))}
            </Slider>
        </div>
    );
};

export default NewsCarousel;
