import React, { useState } from "react";
import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import "./Styles/ProductCarousel.css";

const ProductCarousel = ({ title, images }) => {
    const settings = {
        dots: false,
        infinite: true,
        speed: 500,
        slidesToShow: 3,
        slidesToScroll: 1,
        arrows: true,
    };

    return (
        <div className="product-carousel">
            <h2>{title}</h2>
            <Slider {...settings}>
                {images.map((image, index) => (
                    <div className="product-item" key={index}>
                        <img src={image} alt={`Product ${index + 1}`} />
                    </div>
                ))}
            </Slider>
        </div>
    );
};

const App = () => {
    // Define image sets for the carousel
    const imagesSet1 = [
        "/images/product1.jpg",
        "/images/product2.jpg",
        "/images/product3.jpg",
        "/images/product4.jpg",
    ];

    const imagesSet2 = [
        "/images/productA.jpg",
        "/images/productB.jpg",
        "/images/productC.jpg",
        "/images/productD.jpg",
    ];

    const imagesSet3 = [
        "/images/productX.jpg",
        "/images/productY.jpg",
        "/images/productZ.jpg",
        "/images/productW.jpg",
    ];

    // State to track which image set to display
    const [currentImages, setCurrentImages] = useState(imagesSet1);

    return (
        <div>
            {/* Display the carousel with the current image set */}
            <ProductCarousel title="Product Carousel" images={currentImages} />

            {/* Buttons to switch between different image sets */}
            <div className="image-set-selector">
                <button onClick={() => setCurrentImages(imagesSet1)}>Set 1</button>
                <button onClick={() => setCurrentImages(imagesSet2)}>Set 2</button>
                <button onClick={() => setCurrentImages(imagesSet3)}>Set 3</button>
            </div>
        </div>
    );
};

export default ProductCarousel;
